	public Integer getInteger(Object value){
		if(value instanceof String){
			return Integer.valueOf((String)value);
		}else if(value instanceof Double){
			return ((Long)Math.round((Double)value)).intValue();
		}else if(value instanceof Float){
			return Math.round((Float)value);
		}else if(value instanceof Long){
			return ((Long)value).intValue();
		}else if(value instanceof Integer){
			return (Integer) value;
		}else if(value instanceof Boolean){
			return ((Boolean)value)?1:0;
		}else{
			return 0;
		}
	}
	public Long getLong(Object value){
		if(value instanceof String){
			return Long.valueOf((String)value);
		}else if(value instanceof Double){
			return Math.round((Double)value);
		}else if(value instanceof Float){
			return new Long(Math.round((Float)value));
		}else if(value instanceof Long){
			return (Long) value;
		}else if(value instanceof Integer){
			return new Long((Integer)value);
		}else if(value instanceof Boolean){
			return ((Boolean)value)?1L:0L;
		}else{
			return 0L;
		}
	}
	public Float getFloat(Object value){
		if(value instanceof String){
			return Float.valueOf((String)value);
		}else if(value instanceof Double){
			return ((Double)value).floatValue();
		}else if(value instanceof Float){
			return (Float) value;
		}else if(value instanceof Long){
			return new Float((Long)value);
		}else if(value instanceof Integer){
			return new Float((Integer)value);
		}else if(value instanceof Boolean){
			return ((Boolean)value)?1F:0F;
		}else{
			return 0F;
		}
	}
